head.style:
.zzQuestionDiv{
	position:fixed;
	background-color:#fff;
}
.zzClose{
	position:absolute;
	right:4px;
	top:40px;
	z-index:1;
	color:#d9534f;
}

.weui-flex-footer{
	position:fixed;
	width:100%;
	max-width:750px;
	bottom:0;
	text-align:center;
	border-top:1px solid #ccc;
}
.weui-flex-footer .item{
	background-color:#5cb85c;
	color:#fff;
	padding:10px 0;
	border-right:1px solid #fff;
}
.weui-flex-footer .item:last-child{
	border-right:0;
}

script:
function zzGoBack(){
	var thisURl = '/mobile/onlinequestion/index';
	
    if ((navigator.userAgent.indexOf('MSIE') >= 0) && (navigator.userAgent.indexOf('Opera') < 0)) { // IE 
        if (history.length > 0) {
            window.history.go(-1);
        } else {
            window.location.href = thisURl;
        }
    } else { //非IE浏览器 
        if (navigator.userAgent.indexOf('Firefox') >= 0 ||
            navigator.userAgent.indexOf('Opera') >= 0 ||
            navigator.userAgent.indexOf('Safari') >= 0 ||
            navigator.userAgent.indexOf('Chrome') >= 0 ||
            navigator.userAgent.indexOf('WebKit') >= 0) {
            if (window.history.length > 2) {
                window.history.go(-1);
            } else {
                window.location.href = thisURl;
            }
        } else {
			//未知的浏览器 
            if (history.length > 1) {
                window.history.go(-1);
            } else {
                window.location.href = thisURl;
            }
        }
    }
}

$(function(){
	$('.zzClose').click(function(){
		$('.zzQuestionDiv').hide();
	})
	$('.zzCloseNew').click(function(){
		$('.zzQuestionDiv').hide();
	})
	
	$('.zzNextNew').click(function(){
		var nextID = $(this).attr('data-next');
		if(nextID){
			$('.zzQuestionDiv').hide();
			$('.zzQuestionDiv'+nextID).show();
		}else{
			alert('已经是最后一题');
		}
	})
	
	$('.zzAnswerCardSpan').click(function(){
		var thisID = $(this).attr('data-id');
		$('.zzQuestionDiv').hide();
		$('.zzQuestionDiv'+thisID).show();
	})
	
	$('.buttonUrl').click(function(){
		var thisUrl = $(this).attr('data-url');
		window.location.href = thisUrl;
	})
})

body.style:
.zzAnswerCardUl{
		overflow:hidden;
		margin:10px 0;
	}
	.zzAnswerCardUl li{
		float:left;
		width:20%;
		text-align:center;
		margin:5px 0;
	}
	.zzAnswerCardUl li .zzAnswerCardSpan{
		display:inline-block;
		width:40px;
		height:40px;
		text-align:center;
		line-height:40px;
		border-radius:50%;
		background-color:#ddd;
		color:#555;
	}
	.zzAnswerCardUl li.right .zzAnswerCardSpan{
		background-color:#5cb85c;
		color:#fff;
	}
	.zzAnswerCardUl li.error .zzAnswerCardSpan{
		background-color:#d9534f;
		color:#fff;
	}
	.zzAnswerCardUl li.waring .zzAnswerCardSpan{
		background-color:#f0ad4e;
		color:#fff;
	}
